script.script:
//
//The value of the EvaluationOrder attribute cannot exist in DB for other LifeCycleGraceTimerRule.
//If the value already exists, an error is to be arisen.
var itemFinder = new Finder("cm_spoc.commonWalletUsageRuleFinder");

itemFinder.searchDocument.baseItemCode = "base_LifeCycleGraceTimerRule";
itemFinder.searchDocument.itemFirstAttribute = "itemCode";
itemFinder.searchDocument.itemSecondAttribute = "EvaluationOrder";


var res = itemFinder.search();

for (var i=0; i<res.length;i++){
    var resValue = cm_spoc.itemCodeAttribute(res[i].itemCode , [ "EvaluationOrder" ]);
    if (resValue[0].defaultValue == data.EvaluationOrder){
        errList.addErrorMessage(data,["The value of the EvaluationOrder attribute cannot exist in DB for other LifeCycleGraceTimerRule."],"NGAA_003","E");
        return false;
    }
}
return false;
