script.script:
//The date value EndScheduleRange must be higher than the related attribute value for Start field.

var DS = data.PlanChangeAfterSubscriptionDS;

if(DS.EndScheduleRange <= DS.StartScheduleRange)
{
    errList.addErrorMessage(data,["Daily Schedule Range Ending Time must be higher than Daily Schedule Range Starting Time."],"NGAA_003","E");
}
return false;
