script.script:
//The attribute must be mandatory only if the related attribute assumes the value shown in the column H.
//PromotionDueToExpense
//EndDayOfTheWeek
//PromotionDueToExpense
//WeeklyOrMonthly
// W

var DS = data.PromotionDueToExpenseDS;

if ((!DS.EndDayOfTheWeek) && (DS.WeeklyOrMonthly  == "W"))
