script.script:
//MC
if (data.AwardedTopUpPromotionType=="PT")
    return false;

if(data.AwardedByPermanentTopUpDS){
    var promotion = false;
    for (var i=0;i<data.AwardedByPermanentTopUpDS.ABPTUATUPromPerSegmArrDS.ListDS.length;i++){
        if (data.AwardedByPermanentTopUpDS.ABPTUATUPromPerSegmArrDS.ListDS[i].Promotion){
            promotion = true;
            break;
        }
    }

    if (promotion)
        errList.addErrorMessage(data,["Impossible to save Promotion, AwardedTopUpPromotionType must be set to PT."],"NGAA_003","E");
}

return false;
