script.script:
//The attribute Q0 will not be saved in the catalog only if the PhaseNumber is different of 1.

var currDS1= data.RateRateComponentArrDS.ListDS;

    for(var k =0; k< currDS1.length;k++) {
        var currDS2 = currDS1[k].RCRatePriceEventPhaseArrDS.ListDS;

        for(var i =0; i< currDS2.length;i++) {

            if ((currDS2[i].RatePriceDS.Q0) && currDS2[i].PhaseNumber!=1){
                errList.addErrorMessage(data,["The attribute 'Call Setup' can not be saved in the catalog if the Phase is not 'Initial Cost'"],"NGAA_003","E");
                return false;
            }
        }
    }
return false;
