script.script:
//If the number of the phase is 2 the StartUnit value must be higher than the StartUnit value of the previous phase.
//if phaseNumber = 2 then StartUnit[phaseNumber]> StartUnit[phaseNumber-1]

var currDS1= data.RateRateComponentArrDS.ListDS;

    for(var k =0; k< currDS1.length;k++) {
        var currDS2 = currDS1[k].RCRatePriceEventPhaseArrDS.ListDS;
        for(var i =0; i< currDS2.length;i++) {

            if (currDS2[i].PhaseNumber==2){
                //if (currDS2[i].StartUnit){
                    if (currDS2[i].StartUnit <= currDS2[i-1].StartUnit){
                       errList.addErrorMessage(data,["The attribute 'Time Period from' of Pulse Train 1 must be higher than 'Charge Time' of Initial Cost"],"NGAA_003","E");
                       return false;
                    }
                //}
            }
        }
    }
return false;
