script.script:
//The attribute must be saved only if the related attribute is checked and has the value shown in the column H.
//ServiceTariffPlan
//OriginSrvTariffPlan -- Is Reference Item of ServiceTariffPlanID (Catalog Code attribute)
//PromotionAward
//AwardType
//TC

var DS = data.PromotionDueToExpenseDS.PDTEPromotionAwardArrDS.ListDS;

for(var k =0; k< DS.length;k++) {
   var DS2 = DS[k].AwardSrvTariffChangeDS.ATCSimpAwSrvTCArrDS.ListDS;

   for(var i =0; i< DS2.length;i++) {
       if ((DS2[i].OriginSrvTariffPlan) && (DS[k].AwardType != "TC"))
     {
      errList.addErrorMessage(data,["The attribute 'Origin Service Tariff Plan ID' can be saved only if 'Award Type' is 'Tariff Change'."],"NGAA_003","E");
      return false;
     }
   }
}
return false;
