script.script:
// The attribute must be saved in the catalog only if the related attribute assumes the value shown in the column H.
// TariffPlanGeneralParameterSpecification
// NumberOfTopUps
// TariffPlanGeneralParameterSpecification
// AcceptSOSTopUps
// TRUE

var TariffParam = data.TariffPlanGeneralParametersDS;

if (!TariffParam.NumberOfTopUps && TariffParam.AcceptSOSTopUps==true) {
    errList.addErrorMessage(data,["NumberOfTopUps can be saved only if Accept SOS Top Ups is TRUE"],"NGAA_003","E");
}
return false;
