script.script:
// The attribute must be saved in the catalog only if the related attribute assumes the value shown in the column H.
// TariffPlanGeneralParameterSpecification
// SubscriberCycleDay
// TariffPlanGeneralParameterSpecification
// CycleDayAutomaticCalculate
// FALSE

var TariffParam = data.TariffPlanGeneralParametersDS;

if (!TariffParam.SubscriberCycleDay && TariffParam.CycleDayAutomaticCalculate) {
    errList.addErrorMessage(data,["SubscriberCycleDay can be saved only if CycleDayAutomaticCalculate is FALSE"],"NGAA_003","E");
}
return false;
