script.script:
//The attribute must be saved only if the related attribute is checked and has the value shown in the column H.
//TariffPlan -- CatalogCode attribute!
//TariffPlanIdentifier
//AwardDestinationPlanChange
//ChangeToPreviousPlan
//FALSE

var DS = data.PromotionDueToExpenseDS.PDTEPromotionAwardArrDS.ListDS;

for(var k =0; k< DS.length;k++) {

   if ((DS[k].AwardDestinationPlanChangeDS.TariffPlan) && (DS[k].AwardDestinationPlanChangeDS.ChangeToPreviousPlan != false))
     {
      errList.addErrorMessage(data,["The attribute 'Tariff Plan Identifier' can be saved only if 'Change To Previous Plan' is not checked."],"NGAA_003","E");
      return false;
     }
}
return false;
