script.script:
var result = false;

var SERVER_VERSION = Global.getConfigVariable('SERVER_VERSION');
var TonDescriptionSize = data.TonDescription ? data.TonDescription.length : 0;

if(TonDescriptionSize > 25 && SERVER_VERSION == "v11")
    result = true;

if(result != false)
{
    errList.addErrorMessage(data,["The maximum length for the attribute TonDescription is 25 characters as the AA version is v11"],"NGAA_003","E");
}
return false;
