script.script:
//MC
if (data.AwardedTopUpPromotionType=="PT")
    return false;

if(data.AwardedByPermanentTopUpDS){
    var topUp = false;
    for (var i=0;i<data.AwardedByPermanentTopUpDS.ABPTUATUTUOrConfigArrDS.ListDS.length;i++){
        if (data.AwardedByPermanentTopUpDS.ABPTUATUTUOrConfigArrDS.ListDS[i].TopUpOrigin){
            topUp = true;
            break;
        }
    }

    if (topUp)
        errList.addErrorMessage(data,["Impossible to save TopUpOrigin, AwardedTopUpPromotionType must be set to PT."],"NGAA_003","E");
}

return false;
