var s = new epc.data.Specification();
s.project =  cwa_epc.getDefaultProj();
var arrLen = listDS.length;

for ( var i = 0; i<listDS.length; i++ ) {
    s.specificationCode = listDS[i].BoltOnType;
    var r = epc.getSpecificationByKey(s);
    if(!api_common.isFault(r)){
        if ( listDS[i].UnitsOrPercentageThreshold == "D" &&  !cm_spoc.checkInclusion(r.BoltOnTypeModality,["XN" , "CR" , "PH" ]) )

        errList.addErrorMessage(data,["Invalid value for Units Or Percentage","For this Bolt On Modality"],"NGAA_EPC_002","E");
    }
}
return false;
