script.script:
/*
 * UCRSTypeOfPeriodCondRule.TypeOfPeriod
    UCRelatedSpecification      ValidBoltOnForUsageControl       Exists
    GeneralStartEndDateSpec      StartDateTime       is EMPTY
    GeneralStartEndDateSpec      EndDateTime       is EMPTY
 *
 */

 var top = data.UCRelatedSpecificationDS.TypeOfPeriod;
 var vbuc = data.UCRelatedSpecificationDS.ValidBoltOnForUsageControl;
 var gdsDS = data.GeneralStartEndDateSpecDS;

 if(top) {

     if(!vbuc && (gdsDS.StartDateTime || gdsDS.EndDateTime)) {
         errList.addErrorMessage(data,["Type of period can't be saved if Valid Bolt On For Usage Control is not set and Start and End date are set"],"NGAA_003","E");
     }
 }

 return false;
