script.script:
//The attribute (CFwd) must be saved in the catalog only if the related attribute (AnalysisType) assumes one of the values shown in the column H (2).

var dt=data;

if (dt.CFwd==true && !(dt.AnalysisType == "BNT" || dt.AnalysisType == "C" || dt.AnalysisType == "VC")){
    errList.addErrorMessage(data,["CFwd must be uncheck if Analysis Type is different to BNT, CONTENT or VALUE CONTENT."],"NGAA_003","E");
}
return false;
