script.script:
//The attribute (CheckPortability) must be saved in the catalog only if the related attribute (AnalysisType) assumes one of the values shown in the column H (2, 7, 14).

var dt=data;
if (dt.CheckPortability==true && !(dt.AnalysisType == "BNT" || dt.AnalysisType == "SMS" || dt.AnalysisType == "MMS" || dt.AnalysisType == "C" || dt.AnalysisType == "VC"))
{
    errList.addErrorMessage(data,["Check Portability must be uncheck if Analysis Type is different to BNT, SMS, MMS, CONTENT or VALUE CONTENT."],"NGAA_003","E");
} return false;
