script.script:
//The attribute (DestinationFree) must be saved in the catalog only if the related attribute (AnalysisType) assumes one of the values shown in the column H (2, 5 y 7).

var dt=data;

if (dt.DestinationFree==true && !(dt.AnalysisType == "BNT" || dt.AnalysisType == "B_MT" || dt.AnalysisType == "SMS" || dt.AnalysisType == "C" || dt.AnalysisType == "VC")){
    errList.addErrorMessage(data,["Destination Free must be uncheck if Analysis Type is different to BNT, B_MT, SMS, CONTENT or VALUE CONTENT."],"NGAA_003","E")
}
return false;
