script.script:
//The attribute (MaxLNHZ) must be saved in the catalog only if the related attribute (AnalysisType) assumes one of the values shown in the column H (3).

var dt=data;
if (dt.MaxLNHZ && !(dt.AnalysisType == "ANT" || dt.AnalysisType == "C" || dt.AnalysisType == "VC")) {
    errList.addErrorMessage(data,["MaxLNHZ must be empty if Analysis Tpye is different to ANT, CONTENT or VALUE CONTENT."],"NGAA_003","E");
}
return false;
