script.script:
if( cm_spoc.getExistingItem(data.specificationCode) )
   return false;

var attrArr = {"name":"RuleIdentifier","value":data.RuleIdentifier};
var result = cm_spoc.isUniqueValue("base_WalletUsageRule",[attrArr],data.specificationCode);

if(result != false)
{
    errList.addErrorMessage(data,["Rule Identifier already exists"],"NGAA_003","E");
}
return false;
