script.script:
// Do some actions and prepare the model DS before save
cm_spoc.beforeSaveModelRec(model);

var res=null;
var existingItem = cm_spoc.getExistingItem(model.specificationCode);

if(existingItem && !createMainItem)
    res = cm_common.plmUpdateItem(model, project);
else
    res = cm_common.plmCreateItem(model, project);

return res;
