      var finder = new Finder("cwf_pm:BaseWorklistFinder");
      finder.searchDocument.userId = UserProfile.userId();

      var fr = finder.search();
      for (var i=0; i<fr.length; i++) {
            var item = fr[i];
            var ord = this.getCatalogOrder(item);
            if (ord != null) {
                  var docUI = new cwt_pc.taskResult.UserInterface(null, this);
                  docUI.model.id = item.cwDocId;
                  docUI.model.date = this.getDate(item.DueDate);
                  //docUI.model.task = item.OrderVK;
                  var des = this.getTaskDescription(ord,item);
                  docUI.model.task = des;
                  result[result.length] = docUI;
            }
      }
}
return result;

