script.script:
var entityDoc = null;
for (var i=0; filesGroup.entities && i < filesGroup.entities.length; i++){

    if(entityId != null && entityId != "" ){

        if(filesGroup.entities[i].entityDoc.EntityID ==  entityId){
            entityDoc = filesGroup.entities[i].entityDoc;
            break;
        }

    }
    else {
        if(filesGroup.entities[i].entityDoc.Sequence ==  process.processDocument.ProcessedEntitiesCount){
            entityDoc = filesGroup.entities[i].entityDoc;
            process.processDocument.CurrentEntityID = entityDoc.EntityID;
            break;
        }
    }
}

return entityDoc;
