script.script:
/**
 * Inicialize Success Response (dtree.response)
 *
 * @author  Luis Amorim
 * @date    201403
 *
 * @param {DataStructure}  dtree
 */

switch(dtree.operation)
{
case "queryProduct":
    var response = new DataStructure("OCSProvisioning.productProvisioningManagement.v1.types.QueryProductResponse");
    break;
case "queryProductSubscription":
    var response = new DataStructure("OCSProvisioning.productProvisioningManagement.v1.types.QueryProductSubscriptionResponse");
    break;
case "subscribeProductOffering":
    var response = new DataStructure("OCSProvisioning.productProvisioningManagement.v1.types.SubscribeProductOfferingResponse");
    break;
case "unsubscribeProduct":
    if(dtree.pmOperation[0].activeServiceRegistry && dtree.pmOperation[0].updateServiceRegistry && dtree.response == null)
        var response = new DataStructure("OCSProvisioning.productProvisioningManagement.v1.types.SubscribeProductOfferingResponse");
    else
        var response = new DataStructure("OCSProvisioning.productProvisioningManagement.v1.types.UnsubscribeProductResponse");
    break;
case "updateProduct":
    var response = new DataStructure("OCSProvisioning.productProvisioningManagement.v1.types.UpdateProductResponse");
    break;
}

return response;
